/*Se crea la base de datos */
drop schema if exists valhalla;
drop user if exists ragnar;
CREATE SCHEMA valhalla ;

/*Se crea un usuario para la base de datos*/
create user 'ragnar' identified by 'valhalla123';

/*Se asignan los prvilegios*/
grant all privileges on valhalla.* to 'ragnar';
flush privileges;

/* la tabla de categoria contiene categorias de productos*/
create table valhalla.categoria (
  id_categoria INT NOT NULL AUTO_INCREMENT,
  descripcion VARCHAR(30) NOT NULL,
  ruta_imagen varchar(1024),
  activo bool,
  PRIMARY KEY (id_categoria))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4;

create table valhalla.producto (
  id_producto INT NOT NULL AUTO_INCREMENT,
  id_categoria INT NOT NULL,
  descripcion VARCHAR(30) NOT NULL,  
  detalle VARCHAR(1600) NOT NULL, 
  precio double,
  existencias int,  
  ruta_imagen varchar(1024),
  PRIMARY KEY (id_producto),
  foreign key fk_producto_caregoria (id_categoria) references categoria(id_categoria)  
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4;

create table valhalla.rol (
  id_rol INT NOT NULL AUTO_INCREMENT,
  nombre varchar(20),
  PRIMARY KEY (id_rol)
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4;

/*Se crea la tabla de clientes*/
CREATE TABLE valhalla.usuario (
  id_usuario INT NOT NULL AUTO_INCREMENT,
  id_rol INT,
  username varchar(20) NOT NULL,
  password varchar(512) NOT NULL,
  nombre VARCHAR(20) NULL,
  apellidos VARCHAR(30) NULL,
  correo VARCHAR(25) NULL,
  telefono VARCHAR(15) NULL,
  ruta_imagen varchar(1024),
  PRIMARY KEY (`id_usuario`),
  foreign key fk_usuario_rol(id_rol) references rol(id_rol))  
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4;


CREATE TABLE valhalla.compra (
  id_compra INT NOT NULL AUTO_INCREMENT,
  id_usuario INT NOT NULL,
  fecha_compra TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  total_pagar DOUBLE NOT NULL,
  metodo_entrega ENUM('local', 'domicilio') NOT NULL,
  tienda VARCHAR(50),  -- Usada si el método de entrega es 'local'
  provincia VARCHAR(50),  -- Usada si el método de entrega es 'domicilio'
  canton VARCHAR(50),  -- Usada si el método de entrega es 'domicilio'
  distrito VARCHAR(50),  -- Usada si el método de entrega es 'domicilio'
  datos_adicionales TEXT,  -- Usada si el método de entrega es 'domicilio'
  PRIMARY KEY (id_compra),
  FOREIGN KEY (id_usuario) REFERENCES usuario(id_usuario)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE valhalla.detalle_compra (
  id_detalle INT NOT NULL AUTO_INCREMENT,
  id_compra INT NOT NULL,
  id_producto INT NOT NULL,
  cantidad INT NOT NULL,
  precio_unitario DOUBLE NOT NULL,
  subtotal DOUBLE NOT NULL,
  PRIMARY KEY (id_detalle),
  FOREIGN KEY (id_compra) REFERENCES compra(id_compra),
  FOREIGN KEY (id_producto) REFERENCES producto(id_producto)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


